import React from "react";

const Country = ({data, inputText}) => {
    
    const filteredCountries = data.filter((country) => {
        if (inputText === '') {
            return country;
        }
        else {
            const name = country.name.common
            return name.toLowerCase().includes(inputText);
        }
    });

    return filteredCountries.map((country, index) => <div key={index}>{country.name.common}</div>); 
        {/* <h1>{filteredCountries.name}</h1>
        <p>{filteredCountries.capital}</p>
        <p>area {filteredCountries.area}</p> */} 
}

export default Country;
