import axios from "axios";
const baseURL = "http://localhost:3001/persons";

const getPersons = () => {
    axios
      .get(baseURL)
      .then(response => {
        return response.data;
      });
}

const updatePersons = newPerson => {
    axios
          .post(baseURL, newPerson)
          .then(response => {
            return response.data;
          });
}

export default { getPersons, updatePersons }
