import { useEffect, useState } from 'react';
import axios from 'axios';
import objectsEquality from '../objectsEquality';
import List from "./List";
import Input from "./Input";
import Heading2 from "./Heading2";

const App = () => {
  const [persons, setPersons] = useState([
    { name: 'Arto Hellas', number: '040-123456', id: 1 },
    { name: 'Ada Lovelace', number: '39-44-5323523', id: 2 },
    { name: 'Dan Abramov', number: '12-43-234345', id: 3 },
    { name: 'Mary Poppendieck', number: '39-23-6423122', id: 4 }
  ]);

  const [newName, setNewName] = useState('');
  const [newNumber, setNewNumber] = useState('');
  const [inputText, setInputText] = useState('');

  const hook = () => {
    console.log('effect');
    axios
      .get('http://localhost:3001/persons')
      .then(response => {
        console.log('promise fulfilled');
        setPersons(response.data);
      })
  }

  useEffect(hook, []);

  const addName = (event) => {
    event.preventDefault();

    const newPerson = {
        name: newName,
        number: newNumber
    }

    const personInList = persons.find(person => objectsEquality(person, newPerson));

    if (personInList) {
        alert(`${newPerson.name} is already added to phonebook.`);
    } else {
        setPersons(persons.concat(newPerson));
        setNewName('');
        setNewNumber('');
    }
  }

  const handlePersonChange = (event) => {
    setNewName(event.target.value);
  }

  const handleNumberChange = (event) => {
    setNewNumber(event.target.value);
  }

  const handleFilter = (event) => {
    const lowerCaseInput = event.target.value.toLowerCase();
    setInputText(lowerCaseInput);
  }

  return (
    <div>
      <Heading2 text="Phonebook" />
      <div>
          filter shown with <input onChange={handleFilter}/>
      </div>
      <Heading2 text="Add a new" />
      <form onSubmit={addName}>
        <Input text="name:" value={newName} onChange={handlePersonChange} />
        <Input text="number:" value={newNumber} onChange={handleNumberChange} />
        <button type="submit">add</button>
      </form>
      <Heading2 text="Numbers" />
      <List data={persons} inputText={inputText} />
    </div>
  )
}

export default App
