import React from "react";

const List = ({data, inputText}) => {
    const filteredData = data.filter((person) => {
        if (inputText === '') {
            return person;
        }
        else {
            return person.name.toLowerCase().includes(inputText);
        }
    });

    return filteredData.map(person => <p key={person.name}>{person.name} {person.number}</p>);
}

export default List;
