import React from "react";

const List = ({data, inputText}) => {
    const filteredData = data.filter((person) => {
        if (inputText === '') {
            return person;
        }
        else {
            return person.name.toLowerCase().includes(inputText);
        }
    });

    const handleDelete = (name) => {
        console.log("Clicked ", name);
    }

    return filteredData.map(person => 
        <div>
            <p key={person.name} style={{display: "inline"}}>{person.name} {person.number}</p> 
            <span> </span>
            <button onClick={handleDelete(person.name)} style={{display: "inline"}}>Delete</button>
        </div>
    );
}

export default List;
