import React from "react";

const Message = ({message, messageType}) => {

    const style = {
        backgroundColor: "lightgrey",
        fontSize: 20,
        color: "green",
        borderStyle: "solid",
        borderRadius: 5,
        padding: 10,
        marginBottom: 10
    }

    const redColor = {
        color: "red"
    }

    return <div>
        <p className={style ? messageType === "success" : redColor}>{message}</p>
    </div>
}

export default Message;
